import React, { useState } from 'react';
import minifiguresData from './data/minifigures';
import FiguraCard from './components/FiguraCard';

<div className="relative bg-white shadow-lg mb-8">
  <img
    src="/images/hero.jpg" // reempl치zalo con la imagen que quieras
    alt="LEGO Hero Banner"
    className="w-full max-h-72 object-cover"
  />
  <h1 className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-white text-4xl md:text-6xl font-bold drop-shadow-lg">
    Mi Cat치logo de Minifiguras LEGO
  </h1>
</div>

function App() {
  const [figuras, setFiguras] = useState(minifiguresData);
  const [busqueda, setBusqueda] = useState("");

  const actualizarEstado = (id, nuevoEstado) => {
    setFiguras(figuras.map(f =>
      f.id === id ? { ...f, estado: nuevoEstado } : f
    ));
  };

  const actualizarComentario = (id, nuevoComentario) => {
    setFiguras(figuras.map(f =>
      f.id === id ? { ...f, comentarios: nuevoComentario } : f
    ));
  };

  const figurasFiltradas = figuras.filter(f =>
    f.nombre.toLowerCase().includes(busqueda.toLowerCase()) ||
    f.serie.toLowerCase().includes(busqueda.toLowerCase())
  );

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Cat치logo de Minifiguras LEGO</h1>

      <input
        type="text"
        placeholder="Buscar minifigura..."
        value={busqueda}
        onChange={(e) => setBusqueda(e.target.value)}
        className="w-full p-2 border mb-6 rounded"
      />

      <div className="flex flex-wrap gap-4">
        {figurasFiltradas.map((figura) => (
          <FiguraCard
            key={figura.id}
            figura={figura}
            actualizarEstado={actualizarEstado}
            actualizarComentario={actualizarComentario}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
